  "aggregateCommand": false,
  "newAggregate": false,
  "dependencies": {
    "events": {
      "type": "events",
      "options": {
        "match": {
          "$eventName": "['OpenSpace.OpenSpacePlanned']",
          "openSpaceId": "command.openSpaceId"
        }
      }
    }
  },
  "uiSchema": {
    "openSpaceId": {
      "ui:widget": "hidden"
    },
    "organizer": {
      "ui:widget": "hidden"
    },
    "ui:form": {
      "data": "page|data('/App/OpenSpace')|unset('organizer')"
    }
  },
  "schema": {
    "openSpaceId": "string|format:uuid",
    "name": "string",
    "votingLimit": "integer"
  },
  "rules": [
    {
      "rule": "condition",
      "if": "!events[0]",
      "then": {
        "throw": {
          "error": "'No Open Space planned with this id'"
        }
      },
      "stop": true
    },
    {
      "rule": "condition",
      "if": "events[0].payload.organizer != meta.user.userId",
      "then": {
        "throw": {
          "error": "'User is not allowed to change open space'"
        }
      },
      "stop": true
    },
    {
      "rule": "always",
      "then": {
        "record": {
          "event": "Open Space Changed",
          "mapping": "command"
        }
      }
    }
  ]
}

