{
  "ns": "/App",
  "schema": {
    "$items": {
      "topicId": "string|format:uuid",
      "openSpaceId": "string|format:uuid",
      "title": "string|minLength:1",
      "description": "string",
      "suggestedBy": "string|format:uuid",
      "votes": "number"
    }
  },
  "collection": "topics_collection",
  "uiSchema": {
    "ui:table": {
      "columns": [
        {
          "field": "title",
          "pageLink": "Topic"
        },
        "votes",
        {
          "field": "suggestedBy",
          "headerName": "Suggested By",
          "ref": {
            "data": "/App/AttendeeList",
            "itemIdentifier": "attendee",
            "value": "data.name"
          }
        }
      ]
    }
  },
  "identifier": "topicId",
  "querySchema": {
    "openSpaceId": "string|format:uuid"
  },
  "resolve": {
    "where": {
      "rule": "always",
      "then": {
        "filter": {
          "eq": {
            "prop": "openSpaceId",
            "value": "query.openSpaceId"
          }
        }
      }
    },
    "orderBy": {
      "prop": "votes",
      "sort": "desc"
    }
  },
  "projection": {
    "name": "TopicsProjection",
    "live": true,
    "cases": [
      {
        "when": "Topic Suggested",
        "then": {
          "upsert": {
            "id": "event.topicId",
            "set": "event|set('votes', 0)"
          }
        }
      },
      {
        "given": [
          {
            "rule": "always",
            "then": {
              "find": {
                "information": "OpenSpace.App.Topics",
                "filter": {
                  "docId": "event.topicId"
                },
                "limit": 1,
                "variable": "topics"
              }
            }
          },
          {
            "rule": "condition",
            "if_not": "count(topics)",
            "then": {
              "throw": {
                "error": "'Topic not found with topicId: ' + event.topicId"
              }
            }
          },
          {
            "rule": "always",
            "then": {
              "assign": {
                "variable": "topic",
                "value": "topics[0]"
              }
            }
          }
        ],
        "when": "Topic Voted",
        "then": {
          "update": {
            "filter": {
              "docId": "event.topicId"
            },
            "set": "{votes: topic.votes + 1}"
          }
        }
      }
    ]
  },
  "entity": false
}

