command.metadata:
{
  "aggregateCommand": false,
  "newAggregate": false,
  "dependencies": {
    "OpenSpace.GetOpenSpace": {
      "type": "query",
      "alias": "openSpace"
    },
    "votedEvents": {
      "type": "events",
      "options": {
        "match": {
          "$eventName": "['OpenSpace.TopicVoted']",
          "attendee": "meta.user.userId"
        }
      }
    }
  },
  "uiSchema": {
    "ui:description": "You'll receive a notification when voting limit is reached.",
    "ui:button": {
      "hidden": "!page|data('/App/AttendeeInfo', {isRegistered: false}).isRegistered",
      "disabled": "page|data('/App/OpenSpace', {votingLimit: 0}).votingLimit <= count(page|data('/App/MyVotes', []))"
    },
    "topicId": {
      "ui:widget": "hidden"
    },
    "openSpaceId": {
      "ui:widget": "hidden"
    }
  },
  "schema": {
    "topicId": "string|format:uuid",
    "openSpaceId": "string|format:uuid"
  },
  "rules": [
    {
      "rule": "condition",
      "if": "openSpace.votingLimit <= count(votedEvents)",
      "then": {
        "throw": {
          "error": "'Voting Limit is reached'"
        }
      }
    },
    {
      "rule": "always",
      "then": {
        "record": {
          "event": "Topic Voted",
          "mapping": {
            "$merge": "command",
            "attendee": "meta.user.userId"
          }
        }
      }
    }
  ],
  "streamId": "command.openSpaceId + '_' + meta.user.userId"
}

