policy.metadata:
{
  "rules": [
    {
      "rule": "condition",
      "if": "openSpace.votingLimit <= count(votes)",
      "then": {
        "trigger": {
          "command": "OpenSpace.SendNotification",
          "mapping": {
            "msg": "'You have reached your voting limit for ' + openSpace.name",
            "to": "event.attendee"
          }
        }
      }
    }
  ],
  "dependencies": {
    "OpenSpace.GetOpenSpace": {
      "type": "query",
      "alias": "openSpace"
    },
    "OpenSpace.GetVotes": {
      "type": "query",
      "alias": "votes"
    }
  }
}
